import React, { useState } from 'react';
import axios from 'axios';
import './HomeWorker.css';

function Home() {
  const [selectedImage, setSelectedImage] = useState(null);

  const handleEdit = () => {
    alert("Edit button clicked");
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location = '/';
  };

  const handleImageUpload = async (event) => {
    event.preventDefault();

    if (!selectedImage) {
      alert('Please select an image first.');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      // Adjust the URL based on your backend endpoint
      const response = await axios.post('http://localhost:3001/api/upload-image', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Image uploaded successfully!');
      // Optionally, you can update the UI with the new image URL
      console.log(response.data);
    } catch (error) {
      alert('Error uploading image');
      console.error('Error uploading image:', error);
    }
  };

  const handleImageChange = (event) => {
    setSelectedImage(event.target.files[0]);
  };

  return (
    <div className="home-container">
      <div className="home-header">
        <div className="header-left">
          <div className="dropdown">
            <button className="dropbtn">Setting</button>
            <div className="dropdown-content">
              <button onClick={handleEdit}>Edit</button>
              <button onClick={handleLogout}>Log Out</button>
            </div>
          </div>
        </div>
        <div className="header-right">
          <span>WORKER</span>
        </div>
      </div>
      <div className="home-content">
        <div className="buttons-container">
          <button className="home-button">Add unit test</button>
          <form onSubmit={handleImageUpload}>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
            />
            <button type="submit">Upload Image</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Home;
