import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import './App.css';
import ProfileView from './components/Edit/ProfileView';
import { AuthProvider } from './context/AuthContext';
import Profile from './components/Edit/Profile'; // تأكد من صحة المسار
import Navbar from "./components/Navbar/Navbar";
import Login from "./components/Login";
import Signup from './components/Singup';
import HomePageStudent from './components/HomePageStudent'; 
import HomePageWorker from './components/HomePageWorker'; 
import ForgotPassword from './components/ForgotPassword/ForgotPassword';
import EditProfile from './components/Edit/EditProfile'; 
//import { AuthProvider } from './context/AuthContext'; // Adjust the path as needed
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css'
function App() {
  useEffect(()=>{
axios.get('http://localhost:3001')
  },[]
  )
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.clear();
  };

  return (
    <AuthProvider>

    <Router>
      <div className="App">
        {isLoggedIn && <Navbar onLogout={handleLogout} />}
        <Routes>
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/homePageWorker" element={<HomePageWorker />} />
          <Route path='/signup' element={<Signup />} />
          <Route path='/login' element={<Login />} />
          <Route path='/' element={<Login />} />
          <Route path="/profile" element={<Profile />} /> {/* استخدم مكون Profile هنا */}
          <Route path="/forgot-password" element={<ForgotPassword />} />
          <Route path='/homePageStudent' element={<HomePageStudent />} />
        </Routes>
      </div>
    </Router>
    </AuthProvider>

  );
}

export default App;
