import React, { useState, useEffect } from 'react';
import { AiOutlineLogout } from 'react-icons/ai';
import './HomeWorker.css';
import EditProfile from '../Edit/EditProfile'; // Ensure this path is correct
import { Link, useNavigate } from "react-router-dom";

const generateDefaultImageURL = (email) => {
  const firstLetter = email ? email[0].toUpperCase() : 'U'; // Default to 'U' if email is not available
  return `https://via.placeholder.com/100x100.png?text=${firstLetter}`;
};

function HomeWorker() {
  const [showTable, setShowTable] = useState(false);
  const [unitTestData, setUnitTestData] = useState({
    type: '',
    language: '',
    projectName: '',
    status: 'Pending'
  });
  const [unitTests, setUnitTests] = useState([]);
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [user, setUser] = useState({ username: '', email: '', profileImage: '', firstName: '' });
  const [errorMessage, setErrorMessage] = useState('');
  const [showProfileView, setShowProfileView] = useState(false);

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await fetch('/api/user/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
  
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
  
        const contentType = response.headers.get('content-type');
        if (contentType && contentType.includes('application/json')) {
          const data = await response.json();
          setUser(data); // Update state with the fetched user data
        } else {
          const text = await response.text();
          console.error('Expected JSON but got:', text);
          throw new Error('Expected JSON but got: ' + text);
        }
      } catch (error) {
        console.error('Fetch error:', error);
      }
    };
  
    fetchUserProfile();
  }, []);
  

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUnitTestData({ ...unitTestData, [name]: value });
  };

  const handleAddUnitTest = async () => {
    try {
      const token = localStorage.getItem('token');
      console.log('Adding unit test with data:', unitTestData);
      const response = await fetch('/api/unittests/add', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(unitTestData),
      });
  
      if (!response.ok) {
        const errorText = await response.text();
        console.error('Error response text:', errorText);
        throw new Error(`Network response was not ok: ${errorText}`);
      }
  
      const data = await response.json();
      console.log('Unit test added successfully:', data);
      setUnitTests([...unitTests, data]);
      setUnitTestData({ type: '', language: '', projectName: '', status: 'Pending' });
    } catch (error) {
      setErrorMessage(error.message);
      console.error('Add unit test error:', error);
    }
  };

  const handleSaveAllUnitTests = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/unittests/saveAll', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(unitTests),
      });

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Network response was not ok: ${errorText}`);
      }

      console.log('All unit tests saved successfully');
      setErrorMessage('All unit tests saved successfully');
    } catch (error) {
      setErrorMessage(error.message);
      console.error('Save all error:', error);
    }
  };

  const markAsDone = (index) => {
    const updatedUnitTests = [...unitTests];
    updatedUnitTests[index].status = 'Done';
    setUnitTests(updatedUnitTests);
  };

  const handleCloseEditProfile = () => {
    setShowEditProfile(false);
  };

  const handleToggleTable = () => {
    setShowTable(!showTable);
  };

  const toggleProfileMenu = () => {
    setShowProfileMenu(!showProfileMenu);
  };

  const handleEditProfile = () => {
    setShowEditProfile(true);
  };

  const handleLogout = () => {
    console.log('Logging out');
    window.location.href = '/login'; // Or use routing library
  };

  const handleViewProfile = () => {
    setShowProfileView(true); // This assumes you want to show the profile view
  };

  const handleSaveProfile = async (updatedData) => {
    const token = localStorage.getItem('token');
    
    try {
      const response = await fetch('/api/user/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),
      });
  
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
  
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        const data = await response.json();
        console.log('Profile updated:', data); // Log response
        setUser(data);
        setShowEditProfile(false);
      } else {
        const text = await response.text();
        console.error('Expected JSON but got:', text);
        throw new Error('Expected JSON but got: ' + text);
      }
    } catch (error) {
      console.error('Update error:', error);
      setErrorMessage(error.message);
    }
  };
  
  return (
    <div className="home-worker">
      <header className="home-header">
        <div className="header-left">
          <div className="dropdown">
            <button
              className="dropdown-btn"
              onClick={toggleProfileMenu}
              aria-haspopup="true"
              aria-expanded={showProfileMenu}
            >
              <div className="profile-header">
                <img
                  src={user.profileImage || generateDefaultImageURL(user.email)}
                  alt="Profile"
                  className="profile-image"
                />
                <div className="profile-details">
                  <span className="profile-username">{user.username || 'Loading...'}</span>
                  <span className="profile-email">{user.email || 'Loading...'}</span>
                </div>
              </div>
            </button>

            {showProfileMenu && (
              <div className="profile-menu">
                <div className="profile-info">
                  <img
                    src={user.profileImage || generateDefaultImageURL(user.email)}
                    alt="Profile"
                    className="profile-image"
                  />
                  <p><strong>{user.username || 'Loading...'}</strong></p>
                  <p>{user.email || 'Loading...'}</p>
                </div>
                <button onClick={handleViewProfile}>View Profile</button>
                <button onClick={handleEditProfile}>Edit Profile</button>
                {showEditProfile && (
                  <div className="overlay">
                    <div className="edit-profile-container">
                      <button className="close-button" onClick={handleCloseEditProfile}>
                        &times;
                      </button>
                      <EditProfile
                        user={user}
                        onSave={handleSaveProfile}
                        onCancel={handleCloseEditProfile}
                      />
                    </div>
                  </div>
                )}
              </div>
            )}
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            <span className="logout-icon"><AiOutlineLogout size={24} /></span>
            Log Out
          </button>
          <button className="add-button" onClick={handleToggleTable}>
            {showTable ? 'Hide Table' : '+ New Test'}
          </button>
        </div>
        <div className="header-right">
          <span>Worker</span>
        </div>
      </header>

      {errorMessage && <div className="error-message">{errorMessage}</div>}

      {showTable && (
        <div className="unit-test-form">
          <input
            type="text"
            name="type"
            placeholder="Unit Test Type"
            value={unitTestData.type}
            onChange={handleInputChange}
          />
          <input
            type="text"
            name="projectName"
            placeholder="Project Name"
            value={unitTestData.projectName}
            onChange={handleInputChange}
          />
          <select
            name="language"
            value={unitTestData.language}
            onChange={handleInputChange}
          >
            <option value="" disabled>Select Language</option>
            <option value="Python">Python</option>
            <option value="Java">Java</option>
            {/* Add more languages as needed */}
          </select>
          <Link to="/unit">
              <button type="button" className={add-unit-test-button}>
              Add Unit Test              </button>
            </Link>
          <button className="save-all-button" onClick={handleSaveAllUnitTests}>Save All</button>

        </div>
      )}

      {showEditProfile && (
        <EditProfile
          user={user}
          onSave={handleSaveProfile}
          onCancel={() => setShowEditProfile(false)}
        />
      )}

      {showProfileView && (
        <div className="profile-view">
          <h2>Profile Information</h2>
          <img
            src={user.profileImage || generateDefaultImageURL(user.email)}
            alt="Profile"
            className="profile-image"
          />
          <p><strong>First Name:</strong> {user.firstName }</p>
          <p><strong>Email:</strong> {user.email}</p>
          <button onClick={() => setShowProfileView(false)}>Close</button>
        </div>
      )}
    </div>
  );
}

export default HomeWorker;
