import React, { useState, useEffect } from 'react';
import { AiOutlineLogout } from 'react-icons/ai';
import './HomeWorker.css';
import EditProfile from './EditEditProfile'; // Adjust the path as necessary

const generateDefaultImageURL = (email) => {
  const firstLetter = email ? email[0].toUpperCase() : 'U'; // Default to 'U' if email is not available
  return `https://via.placeholder.com/100x100.png?text=${firstLetter}`;
};

function HomeWorker() {
  const [showTable, setShowTable] = useState(false);
  const [unitTestData, setUnitTestData] = useState({
    type: '',
    language: '',
    status: 'Pending'
  });
  const [unitTests, setUnitTests] = useState([]);
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [user, setUser] = useState({ username: '', email: '', profileImage: '' });
  const [errorMessage, setErrorMessage] = useState('');
  const [editData, setEditData] = useState({ username: '', email: '', profileImage: '' });

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem('token');
      console.log('Token:', token); // Check if token is retrieved

      if (!token) {
        setErrorMessage('No token found');
        return;
      }

      try {
        const response = await fetch('http://localhost:3001/api/user/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }

        const data = await response.json();
        console.log('Fetched data:', data); // Log the fetched data
        setUser(data);
        setEditData({ username: data.username, email: data.email, profileImage: data.profileImage });
      } catch (error) {
        console.error('Fetch error:', error); // Log any errors
        setErrorMessage(error.message);
      }
    };

    fetchUser();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUnitTestData({ ...unitTestData, [name]: value });
  };

  const handleAddUnitTest = () => {
    if (unitTestData.type && unitTestData.language) {
      setUnitTests([...unitTests, unitTestData]);
      setUnitTestData({ type: '', language: '', status: 'Pending' });
    } else {
      setErrorMessage('Please fill out all fields.');
    }
  };

  const markAsDone = (index) => {
    const updatedUnitTests = [...unitTests];
    updatedUnitTests[index].status = 'Done';
    setUnitTests(updatedUnitTests);
  };

  const handleToggleTable = () => {
    setShowTable(!showTable);
  };

  const toggleProfileMenu = () => {
    setShowProfileMenu(!showProfileMenu);
  };

  const handleEditProfile = () => {
    setShowEditProfile(true);
  };

  const handleViewProfile = () => {
    console.log('View profile');
    // Redirect to the profile view page
  };

  const handleLogout = () => {
    // Replace with actual logout logic
    console.log('Logging out');
    window.location.href = '/login'; // Or use routing library
  };

  const handleSaveProfile = async () => {
    const token = localStorage.getItem('token');
    
    try {
      const response = await fetch('http://localhost:3001/api/user/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editData),
      });

      if (!response.ok) {
        throw new Error('Failed to update profile');
      }

      const data = await response.json();
      setUser(data);
      setEditData({ username: data.username, email: data.email, profileImage: data.profileImage });
      setShowEditProfile(false);
    } catch (error) {
      console.error('Update error:', error);
      setErrorMessage(error.message);
    }
  };

  return (
    <div className="home-worker">
      <header className="home-header">
        <div className="header-left">
          <div className="dropdown">
            <button
              className="dropdown-btn"
              onClick={toggleProfileMenu}
              aria-haspopup="true"
              aria-expanded={showProfileMenu}
            >
              <div className="profile-header">
                <img
                  src={user.profileImage || generateDefaultImageURL(user.email)}
                  alt="Profile"
                  className="profile-image"
                />
                <div className="profile-details">
                  <span className="profile-username">{user.username || 'Loading...'}</span>
                  <span className="profile-email">{user.email || 'Loading...'}</span>
                </div>
              </div>
            </button>

            {showProfileMenu && (
              <div className="profile-menu">
                <div className="profile-info">
                  <img
                    src={user.profileImage}
                    alt="Profile"
                    className="profile-image"
                  />
                  <p><strong>{user.username || 'Loading...'}</strong></p>
                  <p>{user.email || 'Loading...'}</p>
                </div>
                <button onClick={handleViewProfile}>View Profile</button>
                <button onClick={handleEditProfile}>Edit Profile</button>
              </div>
            )}
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            <span className="logout-icon"><AiOutlineLogout size={24} /></span>
            Log Out
          </button>
        </div>
        <div className="header-right">
          <span>Worker</span>
        </div>
      </header>

      <div className="top-buttons-container">
        <button className="add-button" onClick={handleToggleTable}>
          {showTable ? 'Hide Table' : 'Add New Unit Test'}
        </button>
      </div>

      {errorMessage && <div className="error-message">{errorMessage}</div>}

      {showTable && (
        <div className="unit-test-form">
          <input
            type="text"
            name="type"
            placeholder="Unit Test Type"
            value={unitTestData.type}
            onChange={handleInputChange}
          />
          <select
            name="language"
            value={unitTestData.language}
            onChange={handleInputChange}
          >
            <option value="" disabled>Select Language</option>
            <option value="Python">Python</option>
            <option value="Java">Java</option>
            {/* Add more languages as needed */}
          </select>
          <button className="add-button" onClick={handleAddUnitTest}>
            Add Unit Test
          </button>
          <table className="unit-test-table">
            <thead>
              <tr>
                <th>Type</th>
                <th>Language</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {unitTests.map((unitTest, index) => (
                <tr key={index}>
                  <td>{unitTest.type}</td>
                  <td>{unitTest.language}</td>
                  <td className={unitTest.status === 'Done' ? 'done' : ''}>
                    {unitTest.status}
                  </td>
                  <td>
                    {unitTest.status !== 'Done' && (
                      <button onClick={() => markAsDone(index)}>Mark as Done</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showEditProfile && (
        <div className="edit-profile-form">
          <h2>Edit Profile</h2>
          <label>
            Username:
            <input
              type="text"
              name="username"
              value={editData.username}
              onChange={(e) => setEditData({ ...editData, username: e.target.value })}
            />
          </label>
          <label>
            Email:
            <input
              type="email"
              name="email"
              value={editData.email}
              onChange={(e) => setEditData({ ...editData, email: e.target.value })}
            />
          </label>
          <label>
            Profile Image URL:
            <input
              type="text"
              name="profileImage"
              value={editData.profileImage}
              onChange={(e) => setEditData({ ...editData, profileImage: e.target.value })}
            />
          </label>
          <button onClick={handleSaveProfile}>Save</button>
          <button onClick={() => setShowEditProfile(false)}>Cancel</button>
        </div>
      )}
    </div>
  );
}

export default HomeWorker
