import React, { useState, useEffect } from 'react';

const EditProfile = () => {
  const [email, setEmail] = useState('');
  const [bio, setBio] = useState('');
  const [profileImage, setProfileImage] = useState(null); 
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetchUserData(); // تأكد من استدعاء الدالة هنا
  }, []);

  // تأكد من تعريف دالة fetchUserData بالشكل الصحيح
  const fetchUserData = async () => {
    try {
      const response = await fetch('/api/get-user', {
        method: 'GET',
      });
      const result = await response.json();
      if (response.ok) {
        setUser(result.user);
        setEmail(result.user.email);
        setBio(result.user.bio);
        setProfileImage(result.user.profileImage);
      } else {
        console.error(result.message);
      }
    } catch (error) {
      console.error('Error fetching user data:', error);
    }
  };

  const handleSaveChanges = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('email', email);
    formData.append('bio', bio);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }

    try {
      const response = await fetch('/api/update-profile', {
        method: 'PUT',
        body: formData,
      });
      const result = await response.json();
      if (response.ok) {
        console.log(result.message);
      } else {
        console.error(result.message);
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  const handleBioChange = (e) => {
    setBio(e.target.value);
  };

  const handleImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  if (!user) return <div>Loading...</div>;

  return (
    <div>
      <h1>Edit Profile</h1>
      <form onSubmit={handleSaveChanges}>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} disabled />
        </div>
        <div>
          <label>Bio:</label>
          <textarea value={bio} onChange={handleBioChange} />
        </div>
        <div>
          <label>Profile Image:</label>
          <input type="file" onChange={handleImageChange} />
        </div>
        <div>
          <img src={`/uploads/${user.profileImage}`} alt="Profile" />
        </div>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditProfile;
