import React, { useState, useEffect } from 'react';

const EditProfile = () => {
  const [email, setEmail] = useState('');
  const [bio, setBio] = useState('');
  const [profileImage, setProfileImage] = useState(null); // لا تنسى إضافة هذا الحقل
  const [user, setUser] = useState(null);

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await fetch('/api/get-user', {
          method: 'GET',
        });

        const result = await response.json();
        if (response.ok) {
          setUser(result.user);
          setEmail(result.user.email);
          setBio(result.user.bio);
          setProfileImage(result.user.profileImage); // تعيين صورة الملف الشخصي
        } else {
          console.error(result.message);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);
  const handleSaveChanges = async () => {
    const formData = new FormData();
    formData.append('email', email);
    formData.append('bio', bio);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }

    try {
      const response = await fetch('/api/update-profile', {
        method: 'PUT',
        body: formData,
      });
      const result = await response.json();
      if (response.ok) {
        console.log(result.message);
        // عرض رسالة نجاح أو توجيه المستخدم
      } else {
        console.error(result.message);
        // عرض رسالة خطأ
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      // عرض رسالة خطأ
    }
  };

  const handleBioChange = (e) => {
    setBio(e.target.value);
  };

  const handleImageChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  if (!user) return <div>Loading...</div>;

  /*const handleBioChange = (event) => {
    setBio(event.target.value);
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setProfileImage(file);
    }
  };
  const handleSaveChanges = async () => {
    const formData = new FormData();
    formData.append('email', email);
    formData.append('bio', bio);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }
  
    try {
      const response = await fetch('/api/update-profile', {
        method: 'PUT',
        body: formData,
      });
      const result = await response.json();
      if (response.ok) {
        console.log(result.message);
        // عرض رسالة نجاح أو توجيه المستخدم
      } else {
        console.error(result.message);
        // عرض رسالة خطأ
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      // عرض رسالة خطأ
    }
  };*/
  
  return (
    <div>
      <h1>Edit Profile</h1>
      {profileImage && <img src={`/uploads/${profileImage}`} alt="Profile" />}
      <form onSubmit={handleSaveChanges}>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </label>
        <label>
          Bio:
          <textarea
            value={bio}
            onChange={handleBioChange}
          />
        </label>
        <label>
          Profile Image:
          <input
            type="file"
            onChange={handleImageChange}
          />
        </label>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default EditProfile;
