import React, { useState, useEffect } from 'react';
import { AiOutlineLogout } from 'react-icons/ai';
import { useNavigate } from 'react-router-dom';
import './HomeWorker.css';
import EditProfile from '../Edit/EditProfile';

function HomeWorker() {
  const [showTable, setShowTable] = useState(false);
  const [unitTestData, setUnitTestData] = useState({
    type: '',
    language: '',
    status: 'Pending'
  });
  const [unitTests, setUnitTests] = useState([]);
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [user, setUser] = useState({ username: '', email: '', profileImage: '' });
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchUserProfile = async () => {
      try {
        const response = await fetch('http://localhost:3001/api/user/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
  
        if (!response.ok) {
          const errorData = await response.json();
          throw new Error(errorData.message || 'Failed to fetch profile data');
        }
  
        const data = await response.json();
        setUser(data);
      } catch (error) {
        console.error('Fetch error:', error);
        setErrorMessage(error.message);
      }
    };
  
    fetchUserProfile();
  }, []);
  
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUnitTestData({ ...unitTestData, [name]: value });
  };

  const navigate = useNavigate();

  const handleViewProfile = () => {
    navigate('/profile');
  };

  const handleAddUnitTest = () => {
    if (unitTestData.type && unitTestData.language) {
      setUnitTests([...unitTests, unitTestData]);
      setUnitTestData({ type: '', language: '', status: 'Pending' });
    } else {
      setErrorMessage('Please fill out all fields.');
    }
  };

  const markAsDone = (index) => {
    const updatedUnitTests = [...unitTests];
    updatedUnitTests[index].status = 'Done';
    setUnitTests(updatedUnitTests);
  };

  const handleToggleTable = () => {
    setShowTable(!showTable);
  };

  const toggleProfileMenu = () => {
    setShowProfileMenu(!showProfileMenu);
  };

  const handleEditProfile = () => {
    setShowEditProfile(true);
  };

  const handleLogout = () => {
    console.log('Logging out');
    window.location.href = '/login';
  };

  const handleSaveProfile = async (updatedData) => {
    try {
      const response = await fetch('http://localhost:3001/api/user/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updatedData),
      });

      if (!response.ok) {
        throw new Error('Failed to update profile');
      }

      const data = await response.json();
      console.log('Profile updated:', data);
      setUser(data);
      setShowEditProfile(false);
    } catch (error) {
      console.error('Update error:', error);
      setErrorMessage(error.message);
    }
  };
const emailInitial = user.email && user.email.length > 0 ? user.email[0].toUpperCase() : 'U';
const profileImageUrl = `https://via.placeholder.com/100x100.png?text=${emailInitial}`;

  return (
    <div className="home-worker">
      <header className="home-header">
        <div className="header-left">
          <div className="dropdown">
            <button
              className="dropdown-btn"
              onClick={toggleProfileMenu}
              aria-haspopup="true"
              aria-expanded={showProfileMenu}
            >
              <div className="profile-header">
              <img
          src={profileImageUrl}
          alt="Profile"
          className="profile-image"
        />

                <div className="profile-details">
                  <span className="profile-username">{user.username || 'Loading...'}</span>
                  <span className="profile-email">{user.email || 'Loading...'}</span>
                </div>
              </div>
            </button>

            {showProfileMenu && (
              <div className="profile-menu">
                <div className="profile-info">
                  <img
                    src={user.profileImage || `https://via.placeholder.com/100x100.png?text=${user.email[0].toUpperCase()}`}
                    alt="Profile"
                    className="profile-image"
                  />
                  <p><strong>{user.username || 'Loading...'}</strong></p>
                  <p>{user.email || 'Loading...'}</p>
                </div>
                <button onClick={handleViewProfile}>View Profile</button>
                <button onClick={handleEditProfile}>Edit Profile</button>
              </div>
            )}
          </div>
          <button className="logout-btn" onClick={handleLogout}>
            <span className="logout-icon"><AiOutlineLogout size={24} /></span>
            Log Out
          </button>
          <button className="add-button" onClick={handleToggleTable}>
            {showTable ? 'Hide Table' : '+ New Test'}
          </button>
        </div>
        <div className="header-right">
          <span>Worker</span>
        </div>
      </header>

      {errorMessage && <div className="error-message">{errorMessage}</div>}

      {showTable && (
        <div className="unit-test-form">
          <input
            type="text"
            name="type"
            placeholder="Unit Test Type"
            value={unitTestData.type}
            onChange={handleInputChange}
          />
          <select
            name="language"
            value={unitTestData.language}
            onChange={handleInputChange}
          >
            <option value="" disabled>Select Language</option>
            <option value="Python">Python</option>
            <option value="Java">Java</option>
            {/* Add more languages as needed */}
          </select>
          <button onClick={handleAddUnitTest}>Add Unit Test</button>
          <table className="unit-test-table">
            <thead>
              <tr>
                <th>Type</th>
                <th>Language</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {unitTests.map((unitTest, index) => (
                <tr key={index}>
                  <td>{unitTest.type}</td>
                  <td>{unitTest.language}</td>
                  <td className={unitTest.status === 'Done' ? 'done' : ''}>
                    {unitTest.status}
                  </td>
                  <td>
                    {unitTest.status !== 'Done' && (
                      <button onClick={() => markAsDone(index)}>Mark as Done</button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {showEditProfile && (
        <EditProfile
          user={user}
          onSave={handleSaveProfile}
          onCancel={() => setShowEditProfile(false)}
        />
      )}
    </div>
  );
}

export default HomeWorker;
