import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import "./Navbar.css";
import logo from '../../assests/images/logo.png'; // Update the path to your logo image

const Navbar = ({ handleLogout }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log("Token:", token); // Check the token value
    setIsLoggedIn(!!token); // Ensure that the state is set correctly if the token exists
  }, []);

  const confirmLogout = () => {
    setShowLogoutConfirm(false);
    performLogout();
  };

  const cancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  const performLogout = () => {
    localStorage.clear();
    window.location = '/login';
  };

  return (
    <>
      <nav>
        <NavLink exact to="/" activeClassName="active-link">
          <img src={logo} alt="Logo" width="150" height="90" />
        </NavLink>

        <div>
          <ul id="navbar">
            <li>
              <h1 exact to="/" activeClassName="active-link">Welcome</h1>
            </li>
            {isLoggedIn ? (
              <li>
                <button
                  className="white_btn"
                  onClick={() => setShowLogoutConfirm(true)}
                  data-testid="Logout-button"
                >
                  Logout
                </button>
              </li>
            ) : (
              <li>
                <NavLink to="/login" className="white_btn" data-testid="Login-button">
                  Login
                </NavLink>
              </li>
            )}
          </ul>
        </div>
      </nav>

      {showLogoutConfirm && (
        <div className="logout-confirm">
          <p>Are you sure you want to logout?</p>
          <button onClick={confirmLogout}>Yes</button>
          <button onClick={cancelLogout}>No</button>
        </div>
      )}
    </>
  );
};

export default Navbar;
