import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './  HomeWorker.css'; // Ensure this path is correct

function HomeWorker() {
  const navigate = useNavigate();
  const [showTable, setShowTable] = useState(false);
  const [tableData, setTableData] = useState({ header: '', content: '' });
  const [tableContent, setTableContent] = useState([]);

  const handleEdit = () => {
    navigate('/edit-profile');
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location = '/';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setTableData({ ...tableData, [name]: value });
  };

  const handleAddRow = () => {
    setTableContent([...tableContent, tableData]);
    setTableData({ header: '', content: '' });
  };

  const handleToggleTable = () => {
    setShowTable(!showTable);
  };

  return (
    <div className="home-container">
      <div className="home-header">
        <div className="header-left">
          <div className="dropdown">
            <button className="dropbtn">Setting</button>
            <div className="dropdown-content">
              <button onClick={handleEdit}>Edit</button>
              <button onClick={handleLogout}>Log Out</button>
            </div>
          </div>
        </div>
        <div className="header-right">
          <span>Worker</span>
        </div>
      </div>
      <div className="home-content">
        <h2>Select a Language</h2>
        <div className="buttons-container">
          <button className="home-button">Python</button>
          <button className="home-button">Java</button>
        </div>
        <button className="home-button" onClick={handleToggleTable}>
          {showTable ? 'Hide Table' : 'Add Table'}
        </button>
        {showTable && (
          <div className="table-form">
            <input
              type="text"
              name="header"
              placeholder="Header"
              value={tableData.header}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="content"
              placeholder="Content"
              value={tableData.content}
              onChange={handleInputChange}
            />
            <button className="home-button" onClick={handleAddRow}>
              Add Row
            </button>
            <table className="content-table">
              <thead>
                <tr>
                  <th>Header</th>
                  <th>Content</th>
                </tr>
              </thead>
              <tbody>
                {tableContent.map((row, index) => (
                  <tr key={index}>
                    <td>{row.header}</td>
                    <td>{row.content}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
}

export default HomeWorker;
