import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import styles from './HomeWorker.css';

const HomeWorker = () => {
  const [workers, setWorkers] = useState([]);
  const [projects, setProjects] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [error, setError] = useState('');
  const [view, setView] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (view === 'workers') fetchWorkers();
    else if (view === 'projects') fetchProjects();
    else if (view === 'tasks') fetchTasks();
  }, [view]);

  const handleEditProfileClick = () => {
    navigate('/edit-profile');
  };

  const fetchWorkers = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:3001/api/workers/all');
      setWorkers(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching workers:', error);
      setError('Failed to fetch workers');
    } finally {
      setLoading(false);
    }
  };

  const fetchProjects = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:3001/api/workers/projects');
      setProjects(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching projects:', error);
      setError('Failed to fetch projects');
    } finally {
      setLoading(false);
    }
  };

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const response = await axios.get('http://localhost:3001/api/workers/tasks');
      setTasks(response.data);
      setError('');
    } catch (error) {
      console.error('Error fetching tasks:', error);
      setError('Failed to fetch tasks');
    } finally {
      setLoading(false);
    }
  };

  const handleViewChange = (view) => {
    setView(view);
  };

  return (
    <div className={styles.container}>
      <div className={styles.buttons}>
        <button className={styles.button} onClick={() => handleViewChange('workers')}>Show Workers List</button>
        <button className={styles.button} onClick={() => handleViewChange('projects')}>Show Projects List</button>
        <button className={styles.button} onClick={() => handleViewChange('tasks')}>Show Tasks List</button>
        <button className={styles.button} onClick={handleEditProfileClick}>Edit Profile</button>
      </div>

      {error && <div className={styles.error}>{error}</div>}

      {loading && <div className={styles.loading}>Loading...</div>}

      {view === 'workers' && (
        <div className={styles.tableContainer}>
          <h2>Workers List</h2>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Email</th>
                <th>Role</th>
              </tr>
            </thead>
            <tbody>
              {workers.map((worker) => (
                <tr key={worker._id}>
                  <td>{worker.firstName}</td>
                  <td>{worker.lastName}</td>
                  <td>{worker.email}</td>
                  <td>{worker.role}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {view === 'projects' && (
        <div className={styles.tableContainer}>
          <h2>Projects List</h2>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>Project Name</th>
                <th>Description</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {projects.map((project) => (
                <tr key={project._id}>
                  <td>{project.name}</td>
                  <td>{project.description}</td>
                  <td>{project.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {view === 'tasks' && (
        <div className={styles.tableContainer}>
          <h2>Tasks List</h2>
          <table className={styles.table}>
            <thead>
              <tr>
                <th>Task Name</th>
                <th>Assigned To</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <tr key={task._id}>
                  <td>{task.name}</td>
                  <td>{task.assignedTo}</td>
                  <td>{task.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default HomeWorker;
