const Navbar = ({ handleLogout }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const isLoggedIn = Boolean(localStorage.getItem("token"));

  const confirmLogout = () => {
    setShowLogoutConfirm(true);
  };
  useEffect(() => {
    const token = localStorage.getItem("token");
    console.log("Token:", token); // تحقق من قيمة التوكن
    setIsLoggedIn(Boolean(token));
  }, []);
  const cancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  const performLogout = () => {
    localStorage.clear();
    window.location = '/login';
  };

  return (
    <>
      <nav>
        <NavLink exact to="/" activeClassName="active-link">
          <img src={logo} alt="Logo" width="150" height="90" />
        </NavLink>

        <div>
          <ul id="navbar">
            <li>
              <h1>أهلاً وسهلاً</h1>
            </li>
            {isLoggedIn ? (
              <li>
                <button
                  className="white_btn"
                  onClick={confirmLogout}
                  data-testid="Logout-button"
                >
                  تسجيل الخروج
                </button>
              </li>
            ) : (
              <li>
                <NavLink to="/login" className="white_btn" data-testid="Login-button">
                  تسجيل الدخول
                </NavLink>
              </li>
            )}
          </ul>
        </div>
      </nav>

      {showLogoutConfirm && (
        <div className="logout-confirm">
          <p>هل أنت متأكد أنك تريد تسجيل الخروج؟</p>
          <button onClick={performLogout}>نعم</button>
          <button onClick={cancelLogout}>لا</button>
        </div>
      )}
    </>
  );
};

export default Navbar;
