import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import './Navbar.css';
import logo from '../../assets/images/logo.png'; // Correct path

const Navbar = ({ handleLogout, userName, userImage }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token); // Ensure that the state is set correctly if the token exists
  }, []);

  const confirmLogout = () => {
    setShowLogoutConfirm(false);
    handleLogout(); // Call the passed handleLogout function
  };

  const cancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  return (
    <>
      <nav className="navbar">
        <NavLink exact to="/" className="navbar-logo">
          <img src={logo} alt="Logo" width="150" height="90" />
        </NavLink>

        <div className="navbar-content">
          <div className="navbar-left">
            <div className="navbar-user">
              <img src={userImage} alt="User" className="user-image" />
              <span className="welcome-message">Welcome, {userName}</span>
            </div>
          </div>
          <div className="navbar-right">
            <NavLink to="/settings" className="navbar-button">Settings</NavLink>
            {isLoggedIn ? (
              <button
                className="navbar-button logout-button"
                onClick={() => setShowLogoutConfirm(true)}
                data-testid="Logout-button"
              >
                Logout
              </button>
            ) : (
              <NavLink to="/login" className="navbar-button" data-testid="Login-button">
                Login
              </NavLink>
            )}
          </div>
        </div>
      </nav>

      {showLogoutConfirm && (
        <div className="logout-confirm">
          <p>Are you sure you want to logout?</p>
          <button onClick={confirmLogout}>Yes</button>
          <button onClick={cancelLogout}>No</button>
        </div>
      )}
    </>
  );
};

export default Navbar;
