import React, { useState, useEffect } from "react";
import { NavLink } from "react-router-dom";
import "./Navbar.css";
import logo from '../../assets/images/logo.png'; // Update the path to your logo image

const Navbar = ({ handleLogout }) => {
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token); // Ensure that the state is set correctly if the token exists
    if (token) {
      // Fetch the user name from localStorage or an API
      const name = localStorage.getItem("userName") || "User";
      setUserName(name);
    }
  }, []);

  const confirmLogout = () => {
    setShowLogoutConfirm(true);
  };

  const cancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  const performLogout = () => {
    localStorage.clear();
    window.location = '/login';
  };

  return (
    <>
      <nav>
        <NavLink exact to="/" activeClassName="active-link">
          <img src={logo} alt="Logo" width="150" height="90" />
        </NavLink>

        <div>
          <ul id="navbar">
            <li>
              <h1>Welcome {isLoggedIn ? userName : ""}</h1>
            </li>
            <li>
              <NavLink to="/developers" className="nav-link" activeClassName="active-link">
                Developers
              </NavLink>
            </li>
            {isLoggedIn ? (
              <>
                <li>
                  <button
                    className="white_btn"
                    onClick={confirmLogout}
                    data-testid="Logout-button"
                  >
                    Logout
                  </button>
                </li>
              </>
            ) : (
              <li>
                <NavLink to="/login" className="white_btn" data-testid="Login-button">
                  Login
                </NavLink>
              </li>
            )}
          </ul>
        </div>
      </nav>

      {showLogoutConfirm && (
        <div className="logout-confirm">
          <p>Are you sure you want to logout?</p>
          <button onClick={performLogout}>Yes</button>
          <button onClick={cancelLogout}>No</button>
        </div>
      )}
    </>
  );
};

export default Navbar;
