import React, { useState } from 'react';
import './EditProfile.css'; // Make sure to create this CSS file
import { useAuth } from '../../context/AuthContext';
import { useNavigate } from 'react-router-dom';

const EditProfile = () => {
  const { token } = useAuth();
  const navigate = useNavigate();

  const [profile, setProfile] = useState({
    name: '',
    email: '',
    bio: '',
    image: null,
  });
  
  const [password, setPassword] = useState({
    oldPassword: '',
    newPassword: '',
  });

  const [error, setError] = useState('');
  const [role, setRole] = useState(''); // Add state for role

  const handleProfileChange = (e) => {
    const { name, value, files } = e.target;
    setProfile({
      ...profile,
      [name]: files ? files[0] : value,
    });
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPassword({
      ...password,
      [name]: value,
    });
  };
  const handleRoleChange = async (newRole) => {
    try {
      const response = await fetch('/api/user/change-role', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ newRole }),
      });

      if (!response.ok) {
        throw new Error('Failed to change role');
      }
      const updatedProfile = await response.json();
      console.log('Role updated:', updatedProfile);
      navigate('/profile'); // Redirect after changing role
    } catch (error) {
      console.error('Failed to change role:', error);
      setError('Failed to change role');
    }
  };
  const handleSaveProfile = async () => {
    if (!profile.name || !profile.email || !profile.bio) {
      setError('Please fill in all profile fields');
      return;
    }
  
    const formData = new FormData();
    formData.append('name', profile.name);
    formData.append('email', profile.email);
    formData.append('bio', profile.bio);
    if (profile.image) {
      formData.append('image', profile.image);
    }
  
    try {
      const response = await fetch('http://localhost:3001/api/user/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
        body: formData,
      });
  
      if (!response.ok) {
        throw new Error('Failed to update profile');
      }
  
      const data = await response.json();
      console.log('Profile updated:', data);
      navigate('/home'); // Redirect after saving
    } catch (error) {
      console.error('Failed to save profile:', error);
      setError('Failed to save profile');
    }
  };

  const handleChangePassword = async () => {
    if (!password.oldPassword || !password.newPassword) {
      setError('Please fill in both password fields');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/api/user/change-password', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(password),
      });

      if (!response.ok) {
        throw new Error('Failed to change password');
      }

      const data = await response.json();
      console.log('Password changed:', data);
      setPassword({ oldPassword: '', newPassword: '' }); // Clear password fields
      navigate('/home'); // Redirect after changing password
    } catch (error) {
      console.error('Failed to change password:', error);
      setError('Failed to change password');
    }
  };

  return (
    <div className="edit-profile-container">
      <div className="edit-profile-form-container">
        <div className="form-group">
          <h1>Edit Profile</h1>
          {error && <div className="error-msg">{error}</div>}
          <div className="form-group">
            <label htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={profile.name}
              onChange={handleProfileChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              value={profile.email}
              onChange={handleProfileChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="bio">Bio</label>
            <textarea
              id="bio"
              name="bio"
              value={profile.bio}
              onChange={handleProfileChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="image">Profile Image</label>
            <input
              type="file"
              id="image"
              name="image"
              onChange={handleProfileChange}
            />
          </div>
          <div className="profile-image-preview">
            {profile.image && (
              <img src={URL.createObjectURL(profile.image)} alt="Profile Preview" />
            )}
          </div>
          <button className="save-button" onClick={handleSaveProfile}>
            Save Profile
          </button>
        </div>

        <div className="form-group">
          <h1>Change Password</h1>
          <div className="form-group">
            <label htmlFor="oldPassword">Old Password</label>
            <input
              type="password"
              id="oldPassword"
              name="oldPassword"
              value={password.oldPassword}
              onChange={handlePasswordChange}
            />
          </div>
          <div className="form-group">
            <label htmlFor="newPassword">New Password</label>
            <input
              type="password"
              id="newPassword"
              name="newPassword"
              value={password.newPassword}
              onChange={handlePasswordChange}
            />
          </div>
          <button className="change-password-button" onClick={handleChangePassword}>
            Change Password
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
