import React, { useState } from 'react';
import './EditProfile.css';

const EditProfile = ({ user, setEditData, handleSaveProfile, handleCancelEdit }) => {
  const [editData, setEditDataState] = useState(user);
  const [selectedFile, setSelectedFile] = useState(null);
  const [errorMessage, setErrorMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditDataState({ ...editData, [name]: value });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setEditDataState({ ...editData, profileImage: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = (e) => {
    e.preventDefault();
    if (!editData.username || !editData.email) {
      setErrorMessage('Please fill out all fields.');
      return;
    }
    handleSaveProfile(editData);
  };

  return (
    <div className="edit-profile-container">
      <div className="edit-profile-form-container">
        <div className="left">
          <h1>Edit Profile</h1>
        </div>
        <div className="right">
          <div className="form_container">
            <h1>Update Information</h1>
            <div className="profile_image_preview">
              <img src={editData.profileImage} alt="Profile" />
            </div>
            <div className="form-group">
              <label>Profile Image</label>
              <input
                type="file"
                name="profileImage"
                accept="image/*"
                onChange={handleFileChange}
              />
            </div>
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                name="username"
                value={editData.username}
                onChange={handleInputChange}
                className="input"
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={editData.email}
                onChange={handleInputChange}
                className="input"
              />
            </div>
            {errorMessage && <p className="error_msg">{errorMessage}</p>}
            <button className="save_button" onClick={handleSave}>
              Save
            </button>
            <button className="save_button" onClick={handleCancelEdit}>
              Cancel
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
