import React, { useState } from 'react';
import axios from 'axios';

function EditProfile() {
  const [profileData, setProfileData] = useState({ email: '', bio: '' });
  const [profileImage, setProfileImage] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProfileData({ ...profileData, [name]: value });
  };

  const handleFileChange = (e) => {
    setProfileImage(e.target.files[0]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('email', profileData.email);
    formData.append('bio', profileData.bio);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }

    try {
      const response = await axios.put('http://localhost:5000/api/update-profile', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert(response.data.message);
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile. Please try again later.');
    }
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Email:</label>
          <input type="email" name="email" value={profileData.email} onChange={handleInputChange} required />
        </div>
        <div>
          <label>Bio:</label>
          <textarea name="bio" value={profileData.bio} onChange={handleInputChange} />
        </div>
        <div>
          <label>Profile Image:</label>
          <input type="file" onChange={handleFileChange} />
        </div>
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
}

export default EditProfile;
