import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EditProfile.css';

const EditProfile = () => {
  const [email, setEmail] = useState('');
  const [bio, setBio] = useState('');
  const [profileImage, setProfileImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);

  useEffect(() => {
    // Fetch the current user's data and populate the state
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:3001/api/get-profile');
        const { email, bio, profileImage } = response.data;
        setEmail(email);
        setBio(bio);
        if (profileImage) {
          setImagePreview(`/uploads/${profileImage}`);
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    fetchUserData();
  }, []);

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleBioChange = (e) => {
    setBio(e.target.value);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setProfileImage(file);

    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('email', email);
    formData.append('bio', bio);
    if (profileImage) {
      formData.append('profileImage', profileImage);
    }
    try {
      const response = await fetch('/api/update-profile', {
        method: 'PUT',
        body: formData
      });
      // Handle successful profile update (e.g., show a success message)
      if (response.ok) {
        // Handle success
        console.log('Profile updated successfully');
      } else {
        // Handle error
        const errorData = await response.json();
        console.error('Error:', errorData.message);
      }
    } catch (error) {
      console.error('Error updating profile:', error);
    }
  };

  return (
    <div className="edit-profile-container">
      <div className="edit-profile-form-container">
        <div className="left">
          <h1>Edit Profile</h1>
          
          {imagePreview && (
            <div className="profile-image-preview">
              <img src={imagePreview} alt="Profile Preview" />
            </div>
          )}
        </div>
        <div className="right">
          <form onSubmit={handleSubmit} className="form-container">
            <h1>Update Your Info</h1>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={handleEmailChange}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="bio">Bio</label>
              <textarea
                id="bio"
                value={bio}
                onChange={handleBioChange}
              />
            </div>
            <div className="form-group">
              <label htmlFor="profileImage">Profile Image</label>
              <input
                type="file"
                id="profileImage"
                accept="image/*"
                onChange={handleImageChange}
              />
            </div>
            <button type="submit" className="save-button">Save Changes</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EditProfile;
