import React, { useState, useEffect } from 'react';
import { AiOutlineLogout } from 'react-icons/ai';
import './HomeWorker.css';
import EditProfile from '../Edit/EditProfile';

function HomeWorker() {
  const [showTable, setShowTable] = useState(false);
  const [unitTestData, setUnitTestData] = useState({
    type: '',
    language: '',
    status: 'Pending'
  });
  const [unitTests, setUnitTests] = useState([]);
  const [showEditProfile, setShowEditProfile] = useState(false);
  const [user, setUser] = useState({ username: '', email: '', profileImage: '' });
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchUser = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setErrorMessage('No token found');
        return;
      }

      try {
        const response = await fetch('http://localhost:3001/api/user/profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }

        const data = await response.json();
        setUser(data);
      } catch (error) {
        setErrorMessage(error.message);
      }
    };

    fetchUser();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUnitTestData({ ...unitTestData, [name]: value });
  };

  const handleAddUnitTest = () => {
    if (unitTestData.type && unitTestData.language) {
      setUnitTests([...unitTests, unitTestData]);
      setUnitTestData({ type: '', language: '', status: 'Pending' });
    } else {
      setErrorMessage('Please fill out all fields.');
    }
  };

  const markAsDone = (index) => {
    const updatedUnitTests = [...unitTests];
    updatedUnitTests[index].status = 'Done';
    setUnitTests(updatedUnitTests);
  };

  const handleEditProfile = () => {
    setShowEditProfile(true);
  };

  const handleLogout = () => {
    console.log('Logging out');
    window.location.href = '/login';
  };

  const handleSaveProfile = async (editData) => {
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('http://localhost:3001/api/user/profile', {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editData),
      });

      if (!response.ok) {
        throw new Error('Failed to update profile');
      }

      const data = await response.json();
      setUser(data);
      setShowEditProfile(false);
    } catch (error) {
      setErrorMessage(error.message);
    }
  };

  const handleCancelEdit = () => {
    setShowEditProfile(false);
  };

  return (
    <div className="signup_container">
      <div className="signup_form_container">
        {showEditProfile ? (
          <EditProfile
            user={user}
            setEditData={setUser}
            handleSaveProfile={handleSaveProfile}
            handleCancelEdit={handleCancelEdit}
          />
        ) : (
          <>
            <div className="left">
              <h1>Worker Dashboard</h1>
              <button className="green_btn" onClick={handleEditProfile}>
                Edit Profile
              </button>
              <button className="white_btn" onClick={handleLogout}>
                <AiOutlineLogout />
              </button>
            </div>
            <div className="right">
              <div className="form_container">
                <h1>Add New Unit Test</h1>
                <input
                  type="text"
                  name="type"
                  value={unitTestData.type}
                  onChange={handleInputChange}
                  className="input"
                  placeholder="Type"
                />
                <input
                  type="text"
                  name="language"
                  value={unitTestData.language}
                  onChange={handleInputChange}
                  className="input"
                  placeholder="Language"
                />
                <button className="green_btn" onClick={handleAddUnitTest}>
                  Add Unit Test
                </button>
                {errorMessage && <p className="error_msg">{errorMessage}</p>}
              </div>
              {showTable && (
                <div className="form_container">
                  <h1>Unit Tests</h1>
                  <table>
                    <thead>
                      <tr>
                        <th>Type</th>
                        <th>Language</th>
                        <th>Status</th>
                        <th>Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {unitTests.map((test, index) => (
                        <tr key={index}>
                          <td>{test.type}</td>
                          <td>{test.language}</td>
                          <td>{test.status}</td>
                          <td>
                            {test.status === 'Pending' && (
                              <button onClick={() => markAsDone(index)}>Done</button>
                            )}
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default HomeWorker;
