import React, { useState } from 'react';
import axios from 'axios';
import './Edit.css';

function Edit() {
  const [image, setImage] = useState(null);
  const [bio, setBio] = useState(''); // New state for bio

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  const handleBioChange = (e) => {
    setBio(e.target.value); // Update bio state
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('image', image);
    formData.append('bio', bio); // Append bio to form data

    try {
      const response = await axios.post('http://localhost:3001/api/update-profile', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('Profile updated successfully');
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile');
    }
  };

  return (
    <div className="edit-container">
      <h2>Edit Profile</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="image">Upload Image</label>
          <input
            type="file"
            id="image"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="bio">Bio</label>
          <textarea
            id="bio"
            value={bio}
            onChange={handleBioChange}
            rows="4"
            placeholder="Tell us about yourself..."
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default Edit;
